import "./App.css";
import React, { useEffect } from "react";
import { useState } from "react";
import * as bookapi from "./BooksAPI"
import MainPage from "./components/MainPage";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [currentlyReading,setCurrentlyReading] = useState([]);
  const [wantToRead,setWantToRead] = useState([]);
  const [read,setRead]= useState([]);
  useEffect(()=>{
    bookapi.getAll().then((data)=>{
const currentlyReading =  data.filter((book)=>book.shelf=== "currentlyReading"
);
const wantToRead =  data.filter((book)=>
  book.shelf=== "wantToRead"
  )
  const read =  data.filter((book)=>
  book.shelf==="read"
  )
  setCurrentlyReading(currentlyReading);
  setWantToRead(wantToRead);
  setRead(read)

}

    )


  },[])
  const moveToShelf = (e,book)=>{
    const shelf = e.target.value;
    bookapi.update(book,shelf).then(data)
  }

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid"></ol>
          </div>
        </div>
      ) : (
       <MainPage read={read}  currentlyReading={currentlyReading} wantToRead={wantToRead} showSearchPage={showSearchPage} setShowSearchpage={setShowSearchpage}/>
      )}
    </div>
  );
}

export default App;
